import './App.css';
import React, {useState} from 'react';
import Alert from './Alert';
import List from './List';

function App() {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);

  const handleSubmit = ()=>{

    setTasks((preTask)=>{
        tasks.map((item)=>{
          return {...preTask, title:  item}
        })
    });
    setTask('');
  }

  console.log(tasks);

  const deleteTask = () =>{
    return 0 
  }

  return (
    <main>
      <section className='container'>
        <Alert></Alert>
        <h2>Grocery Bub</h2>
        <div className='form'>
          <input value={task} type="text" placeholder='e.g. eggs' onChange={event=>setTask(event.target.value)}/>
          <button className='btn btn__submit' type='submit' onClick={handleSubmit}>Submit</button>
        </div>
        {tasks.map((Task, index)=>{
          return(
          <List key={index} />
          )
        })}
        <button type="" className='btn btn__clear' >Clear Items</button>
      </section>
    </main>

  );
}

export default App;
