import './App.css';
import React, {useState} from 'react';
import Alert from './Alert';
import List from './List';

function App() {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);

  const handleSubmit = ()=>{
    setTasks(task);
  }

  return (
    <main>
      <section className='container'>
        <Alert></Alert>
        <h2>Grocery Bub</h2>
        <form>
          <input type="text" placeholder='e.g. eggs' onChange={(event)=>setTask(event.target.value)}/>
          <button className='btn btn__submit' type='submit'>Submit</button>
        </form>
        <button type="" className='btn btn__clear' onClick={handleSubmit}>Clear Items</button>
      </section>
    </main>

  );
}

export default App;
