import './App.css';
import React, {useState} from 'react';
import Alert from './Alert';
import List from './List';

function App() {
  const [alert, setAlert] = useState();
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);

  const handleSubmit = ()=>{
    const count = Math.floor(Math.random()*1000);
    const item = {
      'id': count,
      'title': task
    };
    setTasks((preTask)=>{
      const newTasks = [...preTask, item];
      return newTasks;
    });
    setTask('');
  }



  const deleteTask = () =>{
    return 0 
  }

  return (
    <main>
      <section className='container'>
        <Alert></Alert>
        <h2>Grocery Bub</h2>
        <div className='form'>
          <input value={task} type="text" placeholder='e.g. eggs' onChange={event=>setTask(event.target.value)}/>
          <button className='btn btn__submit' type='submit' onClick={handleSubmit}>Submit</button>
        </div>
        {tasks.map((tasks,index)=>{
          return(
          <List key={index} tasks ={tasks} />
          )
        })}
        <button type="" className='btn btn__clear' >Clear Items</button>
      </section>
    </main>

  );
}

export default App;
